在views.py文件中创建控制器视图：

from django.shortcuts import render, redirect
from .models import MedicalRecord
from .forms import MedicalRecordForm

def index(request):
    records = MedicalRecord.objects.all()
    return render(request, 'medical_records/index.html', {'records': records})

def addRecord(request):
    if request.method == 'POST':
        form = MedicalRecordForm(request.POST)
        if form.is_valid():
            form.save()
            return redirect('index')
    else:
        form = MedicalRecordForm()
    return render(request, 'medical_records/addRecord.html', {'form': form})

def editRecord(request, id):
    record = MedicalRecord.objects.get(pk=id)
    if request.method == 'POST':
        form = MedicalRecordForm(request.POST, instance=record)
        if form.is_valid():
            form.save()
            return redirect('index')
    else:
        form = MedicalRecordForm(instance=record)
    return render(request, 'medical_records/editRecord.html', {'form': form})

def deleteRecord(request, id):
    record = MedicalRecord.objects.get(pk=id)
    record.delete()
    return redirect('index')


